import { TimelineLite, Back, Elastic, Expo } from "gsap";
export default {
  props: {
    loading: {
      type: Boolean,
      default: true,
      required: true,
    },
    spin: {
      type: Boolean,
      required: false,
      default: true,
    },
    spinnerColor: {
      type: String,
      required: false,
      default: '#ffffff',
    },
    pulse: {
      type: Boolean,
      required: false,
      default: true,
    },
    pulseColorLeft: {
      type: String,
      required: false,
      default: '#1565c0',
    },
    pulseColorRight: {
      type: String,
      required: false,
      default: '#149ac36b',
    },
    text: {
      type: String,
      required: false,
    }
  },
  data() {
     return {
        timeline: null,
     };
  },
  mounted() {
     if(this.pulse) {
        this.createTimeline();
     }
  },
  methods: {
     createTimeline() {
        const bubble = this.$refs.bubble;
        const bubblePulse = this.$refs.bubblePulse;
         this.timeline = new TimelineLite({
           onComplete: () => this.timeline.restart()
         });
         this.timeline.to(bubble, 0.4, {
            scale: 0.8,
            rotation: 16,
            ease: Back.easeOut.config(1.7)
         });

         this.timeline.to(bubblePulse, 0.5, {
            scale: 0.9,
            opacity: 1
         },"-=0.6");

        this.timeline.to(bubble, 1.2, {
            scale: 1,
            rotation: "-=16",
            ease: Elastic.easeOut.config(2.5, 0.5)
         });

         this.timeline.to(bubblePulse,1.1,{
            scale: 3,
            opacity: 0,
            ease: Expo.easeOut
         },"-=1.2");
      },
  },
  computed: {
    spinColor() {
      return {'--color': this.spinnerColor};
    },
    pulseCol() {
      return {'--color1': this.pulseColorLeft, '--color2':this.pulseColorRight};
    }
  },
  watch: {
     pulse: {
        immediate: true,
        handler(val, oldVal) {
          if(val === true) {
             this.createTimeline();
          } else {
             if(this.timeline !== null) {
                this.timeline.kill();
                this.timeline = null;
             }
          }
        }
     }
  }
};
